export class AddNewsComponent {
  private readonly apiUrl = 'http://localhost:8080/addNews';

  // Model per il form
  newsArticle: NewsArticle = {
    titolo: '',
    paragrafo:'',
    contenuto: ''
  };

  // Stati del componente
  isSubmitting: boolean = false;
  showSuccessMessage: boolean = false;
  errorMessage: string = '';
  showDebug: boolean = true; // Imposta a false in produzione

  // Lista delle notizie aggiunte
  addedNews: (NewsArticle & { dataCreazione: Date })[] = [];
  showAddedNews: boolean = false;

  constructor(private http: HttpClient, private router: Router) {
    console.log('AddNewsComponent constructor called');
    console.log('Initial newsArticle:', this.newsArticle);
  }

  // Metodi per il conteggio caratteri
  getTitoloLength(): number {
    const length = this.newsArticle.titolo ? this.newsArticle.titolo.length : 0;
    console.log('Titolo length:', length);
    return length;
  }

  getContenutoLength(): number {
    const length = this.newsArticle.contenuto ? this.newsArticle.contenuto.length : 0;
    console.log('Contenuto length:', length);
    return length;
  }

  onInputChange(): void {
    console.log('Input changed - Titolo:', this.newsArticle.titolo, 'Contenuto:', this.newsArticle.contenuto);
  }

  goToHome(): void{
    this.router.navigate(['/']);
  }

  onSubmit(): void {
    console.log('=== onSubmit chiamato ===');
    console.log('newsArticle:', this.newsArticle);
    console.log('isFormValid:', this.isFormValid());

    // Clear previous messages
    this.errorMessage = '';
    this.showSuccessMessage = false;

    if (!this.isFormValid()) {
      this.errorMessage = 'Compila tutti i campi obbligatori';
      console.log('‚ùå Form non valido');
      return;
    }

    this.isSubmitting = true;
    console.log('üöÄ Invio richiesta HTTP...');

    // Simula una chiamata HTTP (commentata per il test)
