  private handleSuccess(): void {
    this.showSuccessMessage = true;
    this.addToAddedNewsList();
    this.resetForm();
    this.isSubmitting = false;

    // Nascondi il messaggio di successo dopo 3 secondi
    setTimeout(() => {
      this.showSuccessMessage = false;
    }, 3000);
  }

  isFormValid(): boolean {
    const titolo = this.newsArticle.titolo?.trim() || '';
    const contenuto = this.newsArticle.contenuto?.trim() || '';

    const valid = titolo.length > 0 && contenuto.length > 0;
    console.log('üîç Validazione form:', {
      titolo: `"${titolo}" (${titolo.length} chars)`,
      contenuto: `"${contenuto}" (${contenuto.length} chars)`,
      valid
    });
    return valid;
  }

  addToAddedNewsList(): void {
    const newsWithDate = {
      ...this.newsArticle,
      dataCreazione: new Date(),
      expanded: false
    };

    this.addedNews.unshift(newsWithDate);

    // Mantieni solo le ultime 5 notizie
    if (this.addedNews.length > 5) {
      this.addedNews = this.addedNews.slice(0, 5);
    }

    console.log('üì∞ Notizia aggiunta alla lista:', newsWithDate);
    console.log('üìö Lista completa notizie:', this.addedNews);
  }

  resetForm(): void {
    console.log('üîÑ Reset form chiamato');
    this.newsArticle = {
      titolo: '',
      paragrafo:'',
      contenuto: ''
    };
    this.errorMessage = '';
    this.showSuccessMessage = false;
    console.log('‚úÖ Form resettato');
  }

  toggleAddedNews(): void {
    this.showAddedNews = !this.showAddedNews;
    console.log('üëÄ Toggle news list:', this.showAddedNews);
  }

  toggleDebug(): void {
    this.showDebug = !this.showDebug;
    console.log('üêõ Toggle debug:', this.showDebug);
  }

  formatDate(date: Date): string {
    return new Intl.DateTimeFormat('it-IT', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    }).format(date);
  }
