export class AppComponent implements OnInit {
  isBrowser = false;

  constructor(
    public auth: AuthService,
    private http: HttpClient,
    @Inject(PLATFORM_ID) private platformId: Object
  ) {}

  ngOnInit() {
    this.isBrowser = isPlatformBrowser(this.platformId);

    if (!this.isBrowser) return;


    this.auth.user$.subscribe(user => {
      if (user) {
        this.http.post('http://localhost:8080/register', {
          auth0Id: user.sub,
          name: user.name,
          email: user.email
        }).subscribe({
          next: () => console.log('Utente salvato'),
          error: err => console.error('Errore nel salvataggio utente', err)
        });
      }
    });


  }

  login() {
    if (this.isBrowser) {
      this.auth.loginWithRedirect();
    }
  }

  logout() {
    if (this.isBrowser) {
      this.auth.logout({ logoutParams: { returnTo: window.location.origin } });
    }
  }

  isMenuOpen = false;


  toggleMenu(): void {
    this.isMenuOpen = !this.isMenuOpen;
  }

  getInitials(name: string | undefined): string {
    if (!name) return 'U';

    return name
      .split(' ')
      .map(word => word.charAt(0))
      .join('')
      .substring(0, 2);
  }


  @HostListener('document:click', ['$event'])
  onDocumentClick(event: Event): void {
    const target = event.target as HTMLElement;
    const menuElement = target.closest('.user-menu');

    if (!menuElement && this.isMenuOpen) {
      this.isMenuOpen = false;
    }
  }
}
